data = FileAttachment("test_table.csv").csv({ typed: true })







viewof Code_of_Practices = Inputs.checkbox(
  ["V1.1", "T2.1", "Q3.2","V1"], 
  { value: ["V1.1"], 
    label: "CoP:"
  }
)







function select(element) {
  return Code_of_Practices.includes(element.Code_of_Practice);
}






data .params({ i: Code_of_Practices }) .filter((d, \() =&gt; op.includes(\).i, d.Code_of_Practice)) .view()

filtered = data.filter(select)

filtered.view()
